mapper.insert:
INSERT INTO t_users
( user_id
, user_name
, password
, salt
)
VALUES
( #{user_id}
, #{user_name}
, #{password}
, #{salt}
)

mapper.update:
UPDATE iw_employees
SET password = #{password}
WHERE seq = #{seq}

UPDATE iw_users
user_name=#{user_name}, password=#{password}, text=#{text},
WHERE user_id = #{user_id}

mapper.select:
SELECT name, email, phone, profile, team_seq, seq, password, pro_seq, salt FROM iw_employees WHERE is_deleted = 'N' AND use_yn = 'Y'
AND (email = #{email} OR seq = #{email})
AND seq = #{seq}

mapper.update:
UPDATE iw_votes
SET status = #{status}
WHERE seq = #{seq}

UPDATE iw_employees 

    updated_date = now(),
     photo = #{file}

WHERE seq = #{seq}

UPDATE iw_career as ic, iw_employees as ie
ic.personal=#{personal}, ic.lastname=#{lastname}, ic.firstname=#{firstname}, ie.name = #{firstname}, ic.birthdate=#{birthdate}, ic.phone=#{phone}, ie.phone = #{phone}, ic.register=#{register}, ic.address=#{address}, ic.family=#{family}, ic.edu=#{edu}, ic.lang=#{lang}, ic.experience=#{experience}, ic.skill=#{skill},
WHERE ie.career_seq = ic.seq AND ic.seq = #{seq}

UPDATE iw_employees
SET team_seq=${team_seq},
pro_seq=${pro_seq},
sort=${sort},
levels=#{levels}
WHERE seq = #{seq}

UPDATE iw_professions SET
pro_name=#{pro_name},
pro_name_ko=#{pro_name_ko}
WHERE seq = #{seq}
